// Updated helper functions using slugs for SEO optimization

// Get blog by slug (primary method for SEO)
export const getBlogBySlug = (slug) => {
  return blogPosts.find((blog) => blog.slug === slug) || null;
};

// Keep the original getBlogById for backward compatibility
export const getBlogById = (id) => {
  const blogId = parseInt(id);
  return blogPosts.find((blog) => blog.id === blogId) || null;
};

// Get similar posts for a specific blog (using slug)
export const getSimilarPostsBySlug = (slug) => {
  const blog = getBlogBySlug(slug);
  if (!blog || !blog.similarPosts) return [];
  
  return blog.similarPosts
    .map(id => getBlogById(id)) // Similar posts are still referenced by ID in the data
    .filter(post => post !== null);
};

// Keep original getSimilarPosts for backward compatibility
export const getSimilarPosts = (blogId) => {
  const blog = getBlogById(blogId);
  if (!blog || !blog.similarPosts) return [];
  
  return blog.similarPosts
    .map(id => getBlogById(id))
    .filter(post => post !== null);
};

// Generate slug from title (utility function)
export const generateSlug = (title) => {
  return title
    .toLowerCase()
    .replace(/[^a-z0-9 -]/g, '') // Remove special characters
    .replace(/\s+/g, '-') // Replace spaces with hyphens
    .replace(/-+/g, '-') // Replace multiple hyphens with single hyphen
    .trim('-'); // Remove leading/trailing hyphens
};



const BASE_URL = "https://pbg.com.np"; // 🔹 update this for production

// Get blog URL using slug
export const getBlogUrl = (blog) => {
  return `${BASE_URL}/blog/${blog.slug}`;
};

// Example usage in SEO helpers or structured data
export const getBlogMetadata = (slug) => {
  const blog = getBlogBySlug(slug);
  if (!blog) return null;

  return {
    title: blog.title,
    description: blog.excerpt,
    keywords: blog.tags.join(", "),
    author: blog.author,
    publishedTime: blog.date,
    category: blog.category,
    image: blog.slides?.[0]?.url || null,
    slug: blog.slug,
    url: getBlogUrl(blog), // uses BASE_URL automatically
  };
};

// You can also use BASE_URL for sitemap or other helpers
export const getBlogSitemapData = () => {
  return blogPosts.map(blog => ({
    url: getBlogUrl(blog), // ✅ absolute URL
    lastModified: blog.date,
    changeFrequency: 'weekly',
    priority: blog.isFeatured ? 0.9 : 0.7
  }));
};



// Get all blog slugs (useful for static generation)
export const getAllBlogSlugs = () => {
  return blogPosts.map(blog => ({
    slug: blog.slug,
    id: blog.id,
    title: blog.title
  }));
};

// Validate slug exists
export const validateSlug = (slug) => {
  return blogPosts.some(blog => blog.slug === slug);
};

// Get next and previous posts by slug (for navigation)
export const getAdjacentPosts = (slug) => {
  const currentIndex = blogPosts.findIndex(blog => blog.slug === slug);
  
  if (currentIndex === -1) return { prev: null, next: null };
  
  const prevPost = currentIndex > 0 ? blogPosts[currentIndex - 1] : null;
  const nextPost = currentIndex < blogPosts.length - 1 ? blogPosts[currentIndex + 1] : null;
  
  return {
    prev: prevPost ? { title: prevPost.title, slug: prevPost.slug } : null,
    next: nextPost ? { title: nextPost.title, slug: nextPost.slug } : null
  };
};

// Get recent posts from separate array
export const getRecentPosts = (limit = 5) => {
  return recentPosts.slice(0, limit);
};

// Get blogs by category from main blog posts
export const getBlogsByCategory = (category, withSlugs = true) => {
  const blogs = blogPosts.filter(blog => blog.category.toLowerCase() === category.toLowerCase());
  
  if (withSlugs) {
    return blogs.map(blog => ({
      ...blog,
      url: getBlogUrl(blog)
    }));
  }
  
  return blogs;
};

// Get recent blogs from main blog posts (based on isRecent flag)
export const getRecentBlogs = (limit = 5, withSlugs = true) => {
  const blogs = blogPosts
    .filter(blog => blog.isRecent)
    .sort((a, b) => new Date(b.date) - new Date(a.date))
    .slice(0, limit);
    
  if (withSlugs) {
    return blogs.map(blog => ({
      ...blog,
      url: getBlogUrl(blog)
    }));
  }
  
  return blogs;
};

