// Search blogs in main blog posts
export const searchBlogs = (query, withSlugs = true) => {
  const lowercaseQuery = query.toLowerCase();
  const blogs = blogPosts.filter(blog => 
    blog.title.toLowerCase().includes(lowercaseQuery) ||
    blog.excerpt.toLowerCase().includes(lowercaseQuery) ||
    blog.tags.some(tag => tag.toLowerCase().includes(lowercaseQuery)) ||
    blog.category.toLowerCase().includes(lowercaseQuery) ||
    blog.slug.includes(lowercaseQuery.replace(/\s+/g, '-'))
  );
  
  if (withSlugs) {
    return blogs.map(blog => ({
      ...blog,
      url: getBlogUrl(blog)
    }));
  }
  
  return blogs;
};

// Get all categories
export const getAllCategories = () => {
  const categories = blogPosts.map(blog => blog.category);
  return [...new Set(categories)];
};

// Get all tags
export const getAllTags = () => {
  const tags = blogPosts.flatMap(blog => blog.tags);
  return [...new Set(tags)];
};

// Get blog statistics
export const getBlogStats = () => {
  const totalPosts = blogPosts.length;
  const totalViews = blogPosts.reduce((sum, blog) => {
    return sum + parseInt(blog.views.replace('k', '000'));
  }, 0);
  const totalLikes = blogPosts.reduce((sum, blog) => {
    return sum + parseInt(blog.likes);
  }, 0);
  const totalComments = blogPosts.reduce((sum, blog) => {
    return sum + parseInt(blog.totalComments);
  }, 0);

  return {
    totalPosts,
    totalViews,
    totalLikes,
    totalComments,
    categories: getAllCategories().length,
    tags: getAllTags().length
  };
};

// Paginate blogs with slug support
export const paginateBlogs = (blogs, page = 1, limit = 10, withSlugs = true) => {
  const startIndex = (page - 1) * limit;
  const endIndex = startIndex + limit;
  
  let paginatedBlogs = blogs.slice(startIndex, endIndex);
  
  if (withSlugs) {
    paginatedBlogs = paginatedBlogs.map(blog => ({
      ...blog,
      url: getBlogUrl(blog)
    }));
  }
  
  return {
    blogs: paginatedBlogs,
    currentPage: page,
    totalPages: Math.ceil(blogs.length / limit),
    totalBlogs: blogs.length,
    hasNext: endIndex < blogs.length,
    hasPrev: page > 1
  };
};



// Generate structured data for SEO
export const getBlogStructuredData = (slug) => {
  const blog = getBlogBySlug(slug);
  if (!blog) return null;
  
  return {
    "@context": "https://schema.org",
    "@type": "BlogPosting",
    "headline": blog.title,
    "description": blog.excerpt,
    "author": {
      "@type": "Person",
      "name": blog.author
    },
    "datePublished": blog.date,
    "dateModified": blog.date,
    "keywords": blog.tags,
    "articleSection": blog.category,
    "wordCount": blog.readTime,
    "image": blog.slides?.[0]?.url || null,
    "url": getBlogUrl(blog)
  };
};


